import React, {useState} from 'react';
import {Link} from 'react-router-dom';
import {cities} from '../data/cities';
import "../styles/Dashboard.css";
import InstagramIcon from '@material-ui/icons/Instagram';
import TelegramIcon from '@material-ui/icons/Telegram';
import TwitterIcon from '@material-ui/icons/Twitter';
import SearchIcon from '@material-ui/icons/Search';
import InputAdornment from '@material-ui/core/InputAdornment';
import InputBase from '@material-ui/core/InputBase';
import IconButton from "@material-ui/core/IconButton";
import { makeStyles } from '@material-ui/core/styles';
const Dashboard = () => {
    const [filterText, setFilterText] = useState("");

    const filteredItems = cities.filter(item =>
        item.name.includes(filterText)
    );
    const itemsToDisplay = filterText ? filteredItems : cities;
    const useStyles = makeStyles((theme) => ({
        root: {
            padding: '2px 4px',
            display: 'flex',
            alignItems: 'center',
            width: 400,
        },
        input: {
            marginLeft: theme.spacing(1),
            flex: 1,
        },
        iconButton: {
            padding: 10,
        },
        divider: {
            height: 28,
            margin: 4,
        },
    }));
    const classes = useStyles();

    return (
        <div className="welcome">
            <div className="textStyle">
                <br/>
                <span> سالیاس! پایگاه خرید و فروش بی‌واسطه‌</span>
                <br/>
                <span>   اگه دنبال چیزی هستی، شهرت رو انتخاب کن و تو دسته‌بندی‌ها به دنبالش بگرد.</span>

                <br/>
                <span> اگر هم می‌خوای چیزی بفروشی، چند تا عکس خوب ازش بگیر و آگهیت رو بچسبون به سالیاس .</span>
                <hr className="hr"/>
                <div>
                    <a href="#"><img alt="3" src="/assert/3.png" className="logo"/></a>
                    <a href="#"><img alt="2" src="/assert/2.png" className="logo"/></a>
                    <a href="#"><img alt="1" src="/assert/1.png" className="logo"/></a>
                </div>
                <hr className="hr"/>
                <div>
                    <span className="bold">  سالیاس </span>
                    <span>را در شبکه های اجتماعی دنبال نمائید:</span>
                </div>
                <div className="">
                    <Link to="/te"><TelegramIcon className="iconsize "/></Link>
                    <Link to="/tw"><TwitterIcon className="iconsize "/></Link>
                    <Link to="/i"><InstagramIcon className="iconsize "/></Link>
                </div>
            </div>

            <Paper component="form" className={classes.root}>
                <IconButton className={classes.iconButton} aria-label="menu">
                    <MenuIcon />
                </IconButton>
                <InputBase
                    className={classes.input}
                    placeholder="Search Google Maps"
                    inputProps={{ 'aria-label': 'search google maps' }}
                />
                <IconButton type="submit" className={classes.iconButton} aria-label="search">
                    <SearchIcon />
                </IconButton>
                <Divider className={classes.divider} orientation="vertical" />
                <IconButton color="primary" className={classes.iconButton} aria-label="directions">
                    <DirectionsIcon />
                </IconButton>
            </Paper>


            <div className="position-input active-cyan-4">
                <InputBase className="form-control mr-3 w-75" type="text"
                       aria-label="Search"
                       placeholder="جستجوی شهر"
                       value={filterText}
                       onChange={e => setFilterText(e.target.value)}
                       InputProps={{
                           startAdornment: (
                               <InputAdornment >
                                   <SearchIcon/>
                               </InputAdornment>
                           ),
                       }}
                />
                <IconButton type="submit"  aria-label="search">
                    <SearchIcon />
                </IconButton>

            </div>

            <div className="position-city">
                {!filteredItems.length && (
                    <div>شهر مورد نظر یافت نشد.</div>
                )}
                {itemsToDisplay.map(item => (
                    <Link key={item.id}>
                        <h3>{item.name}</h3>
                    </Link>
                ))}

            </div>
            <div>

            </div>
        </div>
    );
}


export default Dashboard;
