import React, {useState} from 'react';
import {Link} from 'react-router-dom';
import {cities} from '../data/cities';
import "../styles/Dashboard.css";
import InstagramIcon from '@material-ui/icons/Instagram';
import TelegramIcon from '@material-ui/icons/Telegram';
import TwitterIcon from '@material-ui/icons/Twitter';
import SearchIcon from '@material-ui/icons/Search';
import { makeStyles } from '@material-ui/core/styles';
import InputAdornment from '@material-ui/core/InputAdornment';
import Grid from '@material-ui/core/Grid';
const Dashboard = () => {
    const [filterText, setFilterText] = useState("");

    const filteredItems = cities.filter(item =>
        item.name.includes(filterText)
    );
    const itemsToDisplay = filterText ? filteredItems : cities;
    const useStyles = makeStyles((theme) => ({
        margin: {
            margin: theme.spacing(1),
        },
    }));
    function InputWithIcon() {
        const classes = useStyles();
    }
    return (
        <div className="welcome">
            <div className="textStyle">
                <br/>
                <span> سالیاس! پایگاه خرید و فروش بی‌واسطه‌</span>
                <br/>
                <span>   اگه دنبال چیزی هستی، شهرت رو انتخاب کن و تو دسته‌بندی‌ها به دنبالش بگرد.</span>

                <br/>
                <span> اگر هم می‌خوای چیزی بفروشی، چند تا عکس خوب ازش بگیر و آگهیت رو بچسبون به سالیاس .</span>
                <hr className="hr"/>
                <div>
                    <a href="#"><img alt="3" src="/assert/3.png" className="logo"/></a>
                    <a href="#"><img alt="2" src="/assert/2.png" className="logo"/></a>
                    <a href="#"><img alt="1" src="/assert/1.png" className="logo"/></a>
                </div>
                <hr className="hr"/>
                <div>
                    <span className="bold">  سالیاس </span>
                    <span>را در شبکه های اجتماعی دنبال نمائید:</span>
                </div>
                <div className="">
                    <Link to="/te"><TelegramIcon className="iconsize "/></Link>
                    <Link to="/tw"><TwitterIcon className="iconsize "/></Link>
                    <Link to="/i"><InstagramIcon className="iconsize "/></Link>
                </div>
            </div>

            <div className={classes.margin}>
                <Grid container spacing={1} alignItems="flex-end">
                    <Grid item>
                        <AccountCircle />
                    </Grid>
                    <Grid item>
                        <TextField id="input-with-icon-grid" label="With a grid" />
                    </Grid>
                </Grid>
            </div>

            <div className="position-input active-cyan-4">
                <i className="fas fa-search" aria-hidden="true"></i>
                <input className="form-control mr-3 w-75" type="text"
                       id="input-with-icon-adornment"
                       aria-label="Search"
                       placeholder="جستجوی شهر"
                       value={filterText}
                       onChange={e => setFilterText(e.target.value)}
                       InputProps={{
                           startAdornment: (
                               <InputAdornment position="start">
                                   <SearchIcon  />
                               </InputAdornment>
                           ),
                       }}
                    />

            </div>

            <div className="position-city">
                {!filteredItems.length && (
                    <div>شهر مورد نظر یافت نشد.</div>
                )}
                {itemsToDisplay.map(item => (
                    <Link key={item.id}>
                        <h3>{item.name}</h3>
                    </Link>
                ))}

            </div>
            <div>

            </div>
        </div>
    );
}


export default Dashboard;
