import React, { useState } from "react";
import cities from "../../data/cities";

export  const SearchExample = () => {
    const [filterText, setFilterText] = useState("");

    const filteredItems = cities.filter(
        item =>
            item.name.toLocaleLowerCase().includes(filterText) ||
            item.id.toLocaleLowerCase().includes(filterText)
    );

    const itemsToDisplay = filterText ? filteredItems : cities;

    return (
        <div style={{ padding: "20px 50px" }}>
            <h1>Search Page</h1>
            <input
                type="text"
                placeholder="Filter items by keyword"
                value={filterText}
                onChange={e => setFilterText(e.target.value.toLocaleLowerCase())}
            />
            <hr />
            {!filteredItems.length && (
                <div>There are no items to display adjust your filter criteria</div>
            )}
            {itemsToDisplay.map(item => (
                <div key={item.id}>
                    <h3>{item.name}</h3>

                </div>
            ))}
        </div>
    );
};

export default SearchExample;
